import { useSelector, useDispatch } from "react-redux";
import { userAddress } from "../Redux/User/action";
import AddNewAddress from "./AddNewAddress";
import React, { useState, useEffect } from "react";
import { useHistory, Redirect, Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import { Box, Button, Grid, Paper } from "@material-ui/core";
import SvgIcon from "@material-ui/core/SvgIcon";

function HomeIcon(props) {
  return (
    <SvgIcon {...props}>
      <path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z" />
    </SvgIcon>
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  color: {
    backgroundColor: "white",
    color: "black",
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  navbar: {
    background: "transparent",
    color: "black",
    height: "72px",
    position: "fixed",
    zIndex: 11,
    width: "100%",
    display: "flex",
  },
  imageStyle: {
    height: "28px",
    width: "122px",
  },
  body: {
    border: "none",
    width: "100%",
    height: "auto",
    paddingLeft: "20px",
    margin: "auto",
    marginTop: "4%",
    background: "rgb(240, 245, 247) none repeat scroll 0% 0%",
    overflow: "hidden",
    fontFamily: "sans-serif",
  },
  orderCard: {
    width: "70%",
    height: "80vh",
    margin: "auto",
    borderRadius: "5px",
    marginTop: "48px",
    marginBottom: "50px",
    fontFamily: "sans-serif",
  },
  header: {
    height: "89px",
    background: "rgb(240, 245, 247) none repeat scroll 0% 0%",
    fontFamily: "sans-serif",
  },
  root1: {
    border: "none",
    backgroundColor: theme.palette.background.paper,
    width: "100%",
    display: "flex",
    alignContent: "center",
    height: 671,
    fontFamily: "sans-serif",
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
  paper1: {
    border: "none",
  },
  navStyle: {
    fontWeight: 600,

    fontFamily: "sans-serif",
    border: "none",
  },
  paper2: {
    border: "1px solid rgb(231, 232, 235)",
    minHeight: "108px",
    marginBottom: "18px",
    width: "95%",
    alignItems: "center",
    paddingTop: "35px",
    borderRadius: "5px",
  },

  title1: {
    flexGrow: 1,
    height: "auto",
  },
  paper3: {
    border: "1px solid rgb(231, 232, 235)",
    minHeight: "108px",
    marginBottom: "18px",
    paddingTop: "0px",
    width: "95%",
    // alignItems:'center',
    borderRadius: "5px",
  },
  pic: {
    "& > svg": {
      margin: theme.spacing(2),
    },
  },
  paper4: {
    border: "1px solid rgb(231, 232, 235)",
    minHeight: "78px",
    marginBottom: "18px",
    width: "200px",
    alignItems: "center",
    paddingTop: "25px",
    borderRadius: "5px",
  },
  ButtonBackground: {
    backgroundColor: "rgb(0, 210, 144)",
    borderRadius: "20px",
    padding: "8px 20px",
    fontWeight: "bold",
    fontSize: "12px",
    cursor: "pointer",
    color: "white",
  },
}));
export default function AddressForm() {
  const classes = useStyles();
  const [show, setShow] = React.useState(false);
  const dispatch = useDispatch();
  const [index, setIndex] = React.useState(0);
  React.useEffect(() => {
    dispatch(userAddress());
  }, [index]);
  console.log("showww", show);
  const [address, setAddress] = React.useState(
    useSelector((state) => state.user.userAddresses)
  );
  console.log("addressssss", address);
  return (
    <>
      <Grid container spacing={2}>
        <Grid
          item
          xs={6}
          style={{
            fontSize: "20px",
            textAlign: "left",
            fontWeight: 600,
            fontFamily: "sans-serif",
          }}
        >
          Add delivery address
        </Grid>
        <Grid item xs={6}>
          {" "}
          <button
            style={{ borderRadius: "20px" }}
            onClick={() => setShow(!show)}
            type="button"
            class="btn btn-outline-success"
          >
            Change
          </button>
        </Grid>{" "}
      </Grid>
      <p style={{ fontSize: "12px", color: "#87B2D8", textAlign: "left" }}>
        Choose your delivery address from address book or add new
      </p>
      {!show && (
        <>
          {address.map((item, i) => (
            <div style={{ textAlign: "left" }} key={i}>
              {i === index && (
                <p style={{ textAlign: "left" }}>
                  {item.addressType}
                  {item.street}
                  {item.landmark}
                  {item.place}
                </p>
              )}
            </div>
          ))}
        </>
      )}
      {show && (
        <Grid container>
          <Grid item xs={6}>
            <AddNewAddress />
          </Grid>
          {address.map((item, i) => (
            <Grid item xs={6} key={i}>
              <div
                className={classes.paper3}
                style={{ display: "flex" }}
                onClick={() => {
                  setIndex(i);
                  setShow(false);
                }}
              >
                <div>
                  <IconButton>
                    <HomeIcon fontSize="small" />
                  </IconButton>
                </div>
                <p
                  style={{
                    fontSize: "10px",
                    flexBasis: "90%",
                    textAlign: "justify",
                    fontWeight: 600,
                    fontFamily: "sans-serif",
                    paddingTop: "15px",
                    minHeight: "100px",
                    maxHeight: "80px",
                  }}
                >
                  {item.addressType}
                  <br />
                  {item.street}
                  {item.landmark}
                  {item.actualMapAddress}
                </p>
              </div>
            </Grid>
          ))}{" "}
        </Grid>
      )}
    </>
  );
}
